INSERT INTO `voices` (`id`, `title`, `details`, `audio`, `created_at`, `updated_at`) VALUES
(3, 'ما هي أشهر صخور القمر؟', 'في هذا المقطع الصوتي، ننقل لمسامعكم أشهر الصخور المنتشرة على سطح القمر. طابت مسامعكم! ونتمنى لكم الاستمتاع بعلوم الأرض والجيولوجيا. بصوت: وُد المُعلم', '1662201351.m4a', '2022-07-21 05:54:21', '2022-09-03 10:35:51'),
(4, 'ماهو معنى مصطلح معادن الفلسباثويد؟', 'في هذا المقطع الصوتي، تقدم لكم مبادرة جيوفيليا التعريف الدقيق لمصطلح الفلسباثويد وأشهر معادنه، طابت مسامعكم! ونتمنى لكم الاستمتاع بعلوم الأرض والجيولوجيا. بصوت: وُد المُعلم', '1662201405.m4a', '2022-08-02 00:09:36', '2022-09-03 10:36:45'),
(5, 'ما هو التركيب الداخلي للقمر؟', 'في هذا المقطع الصوتي تقدم لكم مبادرة جيوفيليا لتبسيط العلوم الجيولوجية... التركيب الداخلي للقمر وبنيته التفصيلية. بصوت: هاجر التهامي', '1662201254.m4a', '2022-09-03 10:34:14', '2022-09-03 10:34:14'),
(6, 'ما هي أشهر تضاريس سطح القمر؟', 'في هذا المقطع الصوتي، ننقل لمسامعكم أشهر التضاريس والأشكال الجيومورفولوجية المنتشرة على سطح القمر. طابت مسامعكم! ونتمنى لكم الاستمتاع بعلوم الأرض والجيولوجيا. بصوت: وُد المُعلم', '1664887669.m4a', '2022-10-04 12:40:42', '2022-10-04 12:47:50');

